<?phpclass SiteConfig {	private static $data = array();	private static $datapart = array();	public static function load($id) {		$db = new SQL(0);		$d = @unserialize($db->cmdvalue(0, 'SELECT `data` FROM `config` WHERE `key`="{0}" LIMIT 0,1',array($id)));		if (!is_array($d)) return false;		self::$datapart[$id] = $d;		self::$data = array_merge(self::$data, $d);		return true;	}		/*public static function __get($name) {		return $name;	}		public static function __set($name, $value) {		return $name." => ".$value;	}*/		public static function get($id = null) {		if ($id == null) return self::$data;		return self::$datapart[$id];	}		public static function value($path = "", $id = null) {		$c = self::get($id);		$g = explode("/", str_replace("_","/",$path));		foreach ($g as $a) if (!isset($c[$a])) return null; elseif (is_array($c)) $c = $c[$a];		return $c;	}		public static function val($path = "", $default = "") {		$r = self::value($path, null);		if (!isset($r) OR $r == null) return $default; else return $r;	}		public static function write($id, $data) {		if ($id."" == "") return false;		if (isset(self::$datapart[$id])) self::$datapart[$id] = array_merge(self::$datapart[$id], $data); else self::$datapart[$id] = $data;		self::$data = array_merge(self::$data, $data);		$w = array("key" => $id, "data" => serialize(self::$datapart[$id]));		$db = new SQL(0);		$db->CreateUpdate(0, "config", $w);		return true;	}}